import { createWebHistory, createRouter } from "vue-router";
import Login from '../pages/Login.vue';
import Register from '../pages/Register.vue';
import Home from '../pages/Home.vue';
import About from '../pages/About.vue';
import Promo from '../pages/Promo.vue';
import Menu from '../pages/Menu.vue';
import Table from '../pages/Table.vue';
import Cart from '../pages/Cart.vue';
import Checkout from '../pages/Checkout.vue';
import Thank from '../pages/Thank.vue';
import MyOrder from '../pages/MyOrder.vue';
import Admin from '../admin/Admin.vue';
import Dashboard from '../admin/Dashboard.vue';

const routes = [
  {
    path: "/",
    name: "Home",
    component: Home,
    meta: { title: "Home - Graphic era restaurant" },
  },
  {
    path: "/about",
    name: "About",
    component: About,
    meta: { title: "About Us - Graphic era restaurant" },
  },
  {
    path: "/promotions",
    name: "Promotions",
    component: Promo,
    meta: { title: "Promotions - Graphic era restaurant" },
  },
  {
    path: "/menu",
    name: "Menu",
    component: Menu,
    meta: { title: "Menu - Graphic era restaurant" },
  },
  {
    path: "/table",
    name: "Table",
    component: Table,
    meta: { title: "Table Booking - Graphic era restaurant" },
  },
  {
    path: "/cart",
    name: "Cart",
    component: Cart,
    meta: { title: "Your Cart - Graphic era restaurant" },
  },
  {
    path: "/login",
    name: "Login",
    component: Login,
    meta: { title: "Login - Graphic era restaurant" },
  },
  {
    path: "/register",
    name: "Register",
    component: Register,
    meta: { title: "Register - Graphic era restaurant" },
  },
  {
    path: "/checkout",
    name: "Checkout",
    component: Checkout,
    meta: { title: "Checkout - Graphic era restaurant" },
  },
  {
    path: "/thank",
    name: "Thank",
    component: Thank,
    meta: { title: "Thank You - Graphic era restaurant" },
  },
  {
    path: "/myorder",
    name: "MyOrder",
    component: MyOrder,
    meta: { title: "My Orders - Graphic era restaurant" },
  },
  {
    path: "/admin",
    name: "Admin",
    component: Admin,
    meta: { title: "Admin Login - Graphic era restaurant" },
  },
  {
    path: "/admin/dashboard",
    name: "Dashboard",
    component: Dashboard,
    meta: { title: "Admin Panel - Graphic era restaurant" },
  },
  {
    path: '/:pathMatch(.*)*',
    component: Home,
    meta: { title: "Graphic era restaurant" },
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Dynamic document title based on route
router.afterEach((to) => {
  const defaultTitle = "Graphic era restaurant";
  document.title = to.meta.title || defaultTitle;
});

export default router;
